import React, { Component } from 'react';
import './App.css';
import Button from './Button';
import Panel from './panel';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      counter: 0
    }

    this.addUp = this.addUp.bind(this);
    this.addDown = this.addDown.bind(this);
  }

  addUp() {
    this.setState({
      counter: this.state.counter + 1
    })
  }

  addDown() {
    let counter = this.state.counter;
    counter = counter - 1
    this.setState({
      counter: counter
    })
  }


  render() {
    return (
      <div className="App">
        <h2>Klickzähler</h2>
        <p>{this.state.counter}</p>
        <button onClick={this.addUp}>Klick für + 1</button>
        <button onclick={this.addDown}>Klick für - 1</button>
      </div>
    );
  }
}

export default App;
