import React, { Component } from 'react';
import '../App.css';
import PropTypes from 'prop-types';
import Panel from './panel';
import './TodoAdd.css';

class TodoAdd extends Component {

  constructor(props) {
    super(props);

    this.onTodoAdd = this.onTodoAdd.bind(this);
    this.onTodoInputChange = this.onTodoInputChange.bind(this);

    this.state = {
      newTodo: ""
    }
  }

  onTodoAdd() {
    this.props.onAdd(this.state.newTodo);
  }

  onTodoInputChange(event) {
    this.setState({
      newTodo: event.target.value
    })
  }

  render() {

    return (
      <Panel title="Add Todo-Item">
        <input className='input-field'
                type='text'
                onChange={this.onTodoInputChange}
                value={this.state.newTodo}
                placeholder='Enter Todo-Item'
        />
        {(this.state.newTodo != "" ?(
          <button className='add-btn' onClick={this.onTodoAdd}>Add Todo ({this.state.newTodo.length})</button>
        ) : null)}
      </Panel>
    )
  }
}

TodoAdd.propTypes = {
  onAdd: PropTypes.func.isRequired
}

export default TodoAdd;
