import React, { Component } from 'react';
import './App.css';
import Button from './Button';
import Panel from './panel';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      name: "Welt"
    }


    this.setState = {
      name: "Venus"
    }
  }



  render() {
    let hiAlert = () => {
      this.setState({
        name: "Venus"
      })
    }

    return (
      <div className="App">
        <h2>Hallo {this.state.name}</h2>
        <p>{this.state.name.length}</p>
        <button onClick={hiAlert}>Ver√§ndere den Namen</button>
      </div>
    );
  }
}

export default App;
