import React, { Component } from 'react';
import './App.css';
import Button from './Button';
import Panel from './panel';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      name: "Welt"
    }
  }

  changeName() {
    this.setState({
      name: "Uranus"
    })
  }


  render() {
    let c = () => {
      this.changeName();
    }
    return (
      <div className="App">
        <h2>Hallo {this.state.name}</h2>
        <p>{this.state.name.length}</p>
        <button onClick={c}>Ver√§ndere den Namen</button>
      </div>
    );
  }
}

export default App;
