import React, { Component } from 'react';
import './App.css';
import Button from './Button';
import Panel from './panel';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      counter: 0
    }

    this.changeName = this.changeName.bind(this);
  }

  changeName() {
    this.setState({
      counter: + 1
    })
  }


  render() {
    return (
      <div className="App">
        {/* <h2>Hallo {this.state.name}</h2> */}
        <h2>Klickzähler</h2>
        <p>{this.state.counter}</p>
        <button onClick={this.changeName}>Klick für + 1</button>
      </div>
    );
  }
}

export default App;
