import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      todos: [
        "Shopping",
        "Doing Laundry",
        "Watching TV"
      ]
    }
    this.addTodo = this.addTodo.bind(this);
  }

  addTodo() {
    let todos = this.state.todos;
    todos.push("Next Todo");
    this.setState({
      todos: todos
    })
  }

  render() {
    return (
      <div className="App">
        <h1>Todo-List</h1>
        <ul className='App-todo'>
          {this.state.todos.map((todo) => {
            return (
              <li>{todo}</li>
            )
          })}
        </ul>
        <button onClick={this.addTodo}>Todo hinzuf√ºgen</button>
      </div>
    );
  }
}

export default App;
